import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";

interface FileInfo {
  name: string;
  lines: number;
  purpose: string;
  type: "python" | "html" | "css" | "javascript" | "config";
}

interface FileInfoTableProps {
  files: FileInfo[];
}

